mapper.select:
SELECT house.id, user_id AS userId, type_id AS typeId, title, description, price, pubdate, floorage, contact, street_id AS streetId, type.`NAME` AS tname, street.`NAME` AS sname, district.`NAME` AS dname FROM house LEFT JOIN `type` ON house.`TYPE_ID`=type.`ID` LEFT JOIN street ON house.`STREET_ID`= street.`ID` LEFT JOIN `district` ON street.`DISTRICT_ID`=district.`ID` where 1=1
and title like concat('%',#{title},'%')
and district.id=#{did}
and street.id=#{sid}
and type_id=#{tid}
and price > #{startPrice}
and price < #{endPrice}
